    # Méthode pour supprimer les @#

    def remove_a(list)
        list.map { |w| w.gsub('@', '') }
    end

    # Méthode pour compter le nombre de caractère et renvoyer un entier#

    def char_number(list)
        nbr_char = []
        list.each { |x| nbr_char << x.length}                
        return nbr_char
    end
    
    # Méthode pour compter les occurences et en faire la somme#

    def repartition(list)
        x = char_number(list)
        y = 0
        while y < (x.each.max)
            a =  x.each.count(y)
            if a > 0 
                puts "#{a} handles avec #{y} caractères."
                y += 1
            else
                y+=1
            end
        end    
    end

    #Perform avec le menu#

def perform(list)
    puts "> 1 < Nombre de journaliste dans le tableau."
    puts "> 2 < Le handle le plus court du tableau."
    puts "> 3 < nombre de handle contenant 5 caractère."
    puts "> 4 < Handle qui commence avec une majuscule."
    puts "> 5 < Trie le tableau en ordre alphabetique."
    puts "> 6 < Trie le tableau du plus petit au plus grand."
    puts "> 7 < Trouve epenser dans le tableau."
    puts "> 8 < Répartition des handles par nombre de caractère."
    puts "   > N'importe quoi d'autre pour quitter.<"
    n = gets.chomp.to_i
        while n >= 1 && n <=8
            if n == 1 
                puts "Il y a #{list.count} journalistes dans ce tableau."

            elsif n == 2 
                puts "Le handle le plus court est #{list.min_by(&:length)}"

            elsif n == 3
            without = remove_a(list)
            nbr = char_number(without)
            puts "Il y a #{nbr.count(5)} handles contenants 5 caractères." 

            elsif n == 4
            without = remove_a(list)
            puts "Il y a #{without.grep(/^[A-Z]/).count} handles qui debutent avec une majuscule."

            elsif n == 5 
            puts list.sort

            elsif n == 6
            puts list.sort_by(&:length)

            elsif n == 7
                puts "Epenser se trouve à la  #{list.find_index("@epenser")}ème postion" 

            elsif n == 8
                repartition(list)
            end
            puts "Que veux tu faire d'autre?"
            n = gets.chomp.to_i
        end
end

perform(list)